package com.carnine.solution;public class Calculator {    private static int sum;    /* Constructor */    public Calculator() {        sum = 0;    }    /* Performs Addition. Accepts an int Array. Returns an int */    public int add(int[] arr) {        for (int i = 0; i < arr.length; i++)            sum+=arr[i];        return sum;    }    /* Accepts a Striing Array, and delimeter. Removes newline chars and replaces with delimeter. Returns an int Array */    public int[] cleanUpStringArrayToInt(String[] arr, String delim){        for (int i = 0; i < arr.length; i++) {           String newVal = arr[i].replace("\r\n", "")                    .replaceAll("\r", "")                   .replaceAll("\r", "")                    .replaceAll(" ",",");           arr[i]=newVal;        }        String stringToSplit = arr[0];        /* String to split. */        String[] tempArray;        /* delimiter */        tempArray = stringToSplit.split(delim);        int[] intArray = new int[tempArray.length];        for (int i = 0; i < tempArray.length; i++) {            String numberAsString = tempArray[i];            intArray[i] = Integer.parseInt(numberAsString);        }        return intArray;    }}